(function () {
	"use strict";

	const navList       = document.getElementById("navigation"),
	  	  items         = navList.getElementsByTagName("a"),
	  	  sliderElement = document.getElementById("slider"),
		  slides       	= ["img/slide1.jpg",
		  				   "img/slide2.jpg",
		  				   "img/slide1.jpg",
		  				   "img/slide2.jpg"];

	setSliderImage(0);
	a();

	function a() {
		for (let item of items) {
			item.addEventListener("click", setSlide);
		}		
	}

	function setSlide(event) {
		const numberSlide = this.innerHTML - 1;

		deleteActiveClass();
		setSliderImage(numberSlide);
		items[numberSlide].classList.add("active");
	}

	function setSliderImage(index) {
		sliderElement.style.backgroundImage = `url(${slides[index]})`;
	}

	function deleteActiveClass() {
		for (let item of items) {
			item.classList.remove("active");
		}
	}
})();
